 let TipDivs = function(props) {
   
    function activated() {
              
    let inputedValue = document.querySelector(".inputone").value;
    console.log(inputedValue);

    let numOfPeople = document.querySelector(".inputtwo").value;

    let screnValue = document.querySelectorAll('.firstScreen');
    console.log(screnValue);

    
       


        if(props.amount === 5){ 
            let tip = inputedValue * 0.05;
            screnValue[0].textContent = (inputedValue * 0.05)/numOfPeople;
            screnValue[1].textContent = (inputedValue + tip)/ numOfPeople;

          console.log('5 Clicked');
        }
      }

    return (
        <div onClick={() => {
            activated();
        }} className="tipDiv">{props.amount}</div>
    )
}


export default TipDivs;
