import React, { useState } from 'react';
import Division from './balls';
function App() {

  const [color, setColor] = useState('red');
  console.log('red');


   let colChanger = function() {
    setColor(color === 'black');
    console.log(color);
   }
  return (
    <div>
      <Division onclick={colChanger} setColor={setColor} num={1} value={1}/>
      <Division setColor={setColor}   num={2} value={2}/>
      <Division setColor={setColor} num={3} value={3}/>
      <Division setColor={setColor}  num={4} value={4}/>
      <Division setColor={setColor}  num={5} value={5}/>
    </div>
  )

}





export default App;
