import { useState } from "react";



 let TipDivs = function(props) {
      
    const [isActive, setActive] = useState("false");
    const ToggleClass = () => {
      setActive(!isActive); 
      console.log('hello');
     };
    

    return (
        <div onClick={ToggleClass}  className={isActive ? "tipDiv" : "tipDivActive"}>{props.amount} %</div>
    )
}


export default TipDivs;
