import React, { useState } from 'react';

function App() {
  const [selectedGrade, setSelectedGrade] = useState(null);

  const handleGradeClick = (grade) => {
    setSelectedGrade(grade);
  };

  return (
    <div>
      <Grades selectedGrade={selectedGrade} onGradeClick={handleGradeClick} />
    </div>
  );
}

function Grades(props) {
  const grades = [1, 2, 3, 4, 5];

  return (
    <div>
      {grades.map((grade) => (
        <span
          key={grade}
          className={props.selectedGrade === grade ? 'selected' : ''}
          onClick={() => props.onGradeClick(grade)}
        >
          {grade}
        </span>
      ))}
    </div>
  );
}

export default App;
