import React, { useState } from "react";
import TipDivs from "./tipdivisions.js";
import InpComps from "./inputcomps.js";
function App() {

  function activated() {

    let inputedValue = document.querySelector(".inputone").value;
    console.log(inputedValue);

    let numOfPeople = document.querySelector(".inputtwo").value;

    let screnValue = document.querySelectorAll('.firstScreen');
    console.log(screnValue);

    // let pr = props.amount;

      // function calc(pr){
      //   let tip = inputedValue * (pr/100);
      //       let total = tip + inputedValue;
      //       screnValue[0].textContent = tip/numOfPeople;
      //       screnValue[1].textContent = total/numOfPeople;

      // }

      // calc(pr);

      }



  return (
    <div className="mainDiv">
      <div className="box">
        <div className="keys">
          <div className="screen">
            <div className="bill">
              <form>
                <label>Bill</label>
                <input className="inputone" placeholder="$"></input>
              </form>
            </div>
            <div className="tipAmount">
              <label>Select Tip % </label>
              <div className="positioner">
                <TipDivs active={activated}  amount={5} />
                <TipDivs active={activated} amount={10} />
                <TipDivs amount={15} />
                <TipDivs amount={25} />
                <TipDivs amount={50} />
                <TipDivs amount={5} />
              </div>
            </div>
            <div className="people">
              <form>
                <label>Number Of People</label>
                <input className="inputtwo" placeholder=""></input>
              </form>
            </div>
          </div>
        </div>
        <div className="display">
          <div className="sc">
            <div className="screenparts">
              <div className="amount"> 
              <p>Tip amount</p> <InpComps/>
              </div>
              <div className="total">
              <p>Total</p> <InpComps/>
                 </div>

            </div>
            <div className="screenbtn"> </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
