import React, { useState, useTransition } from "react";
import TipDivs from "./tipdivisions.js";
import InpComps from "./inputcomps.js";
function App() {
  const [tip, setTip] = useState(0);
  const [bill, setBill] = useState(150);
  const [people, setPeople] = useState(5);
  const [percen, setPercent] = useState(0);

  function activated() {
     setPercent(0.05);
    console.log('Should be 5')

  }

  function ten(){
    setPercent(0.10);
    console.log('Should be 10')

  }

  function changed(e){
    let val = e.target.value;
    console.log(val)
    let total = val * percen;
    setBill(total);
  }

  return (
    <div className="mainDiv">
      <div className="box">
        <div className="keys">
          <div className="screen">
            <div className="bill">
              <form>
                <label>Bill</label>
                <input onChange={changed} className="inputone" placeholder="$"></input>
              </form>
            </div>
            <div className="tipAmount">
              <label>Select Tip % </label>
              <div className="positioner">
                <TipDivs active={activated} amount={5} />
                <TipDivs active={ten} amount={10} />
                <TipDivs active={activated} amount={15} />
                <TipDivs active={activated} amount={25} />
                <TipDivs active={activated} amount={50} />
                <TipDivs active={activated} amount={5} />
              </div>
            </div>
            <div className="people">
              <form>
                <label>Number Of People</label>
                <input className="inputtwo" placeholder=""></input>
              </form>
            </div>
          </div>
        </div>
        <div className="display">
          <div className="sc">
            <div className="screenparts">
              <div className="amount">
                <p>Tip amount</p> <InpComps value={tip} />
              </div>
              <div className="total">
                <p>Total</p> <InpComps value={bill} />
              </div>
            </div>
            <div className="screenbtn"> </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
