        console.log('1');
        document.body.style.backgroundColor = 'red';
        item[0].style.backgroundColor = 'purple';
        
      }
      if(props.value === 2 ) {
        document.body.style.backgroundColor = 'black';
        console.log(props.value);
      }
      if(props.value === 3){
        item = "yellow";
      }
    
}

    return (
        <button onClick={functionality} className="balDiv"  value={props}>{props.num}</button>
    )
}



