import React, { useState, useTransition } from "react";
import TipDivs from "./tipdivisions.js";
import InpComps from "./inputcomps.js";
function App() {

  const [tip, setTip] = useState(0);
  const [bill, setBill] = useState(0);
  const [people, setPeople] = useState(5);
  const [percen, setPercent] = useState(0.05);
  const [startbill, setStartBill] = useState(0);


  function updater() {
    let total = (startbill + startbill * percen) / people;
    setBill(total);

  }
  function activated(e) {
    setPercent(0.05);

  }

  function ten() {
    setPercent(0.1);
    updater()

  }
  function peopleNum(e) {
    setPeople(parseInt(e.target.value));
    updater()


  }

  function generator(e) {
    setStartBill(parseInt(e.target.value));
    console.log(startbill);
    updater()

  }



  return (
    <div className="mainDiv">
      <div className="box">
        <div className="keys">
          <div className="screen">
            <div className="bill">
              <form>
                <label>Bill</label>
                <input
                  type="number"
                  onChange={generator}
                  className="inputone"
                  placeholder="$"
                ></input>
              </form>
            </div>
            <div className="tipAmount">
              <label>Select Tip % </label>
              <div className="positioner">
                <TipDivs active={activated} amount={5} />
                <TipDivs active={ten} amount={10} />
                <TipDivs active={activated} amount={15} />
                <TipDivs active={activated} amount={25} />
                <TipDivs active={activated} amount={50} />
                <TipDivs active={activated} amount={5} />
              </div>
            </div>
            <div className="people">
              <form>
                <label>Number Of People</label>
                <input
                  type="number"
                  onChange={peopleNum}
                  className="inputtwo"
                  placeholder=""
                ></input>
              </form>
            </div>
          </div>
        </div>
        <div className="display">
          <div className="sc">
            <div className="screenparts">
              <div className="amount">
                <p>Tip amount</p> <InpComps value={tip} />
              </div>
              <div className="total">
                <p>Total</p> <InpComps value={bill} />
              </div>
            </div>
            <div className="screenbtn"> </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
