import React, { useState } from "react";
import Division from "./balls";
function App() {
  const [color, setColor] = useState("red");
  console.log("red");

   function items () {
    let item = document.querySelectorAll(".balDiv");
    item[1].style.backgroundColor = color;
  };

  colChanger();

  return (
    <div>
      <Division
        increase={() => {
          setColor(color === "black");
        }}
        setColor={setColor}
        num={1}
        value={1}
      />
      <Division
        increase={() => {
          setColor(color == "yellow");
        }}
        setColor={setColor}
        num={2}
        value={2}
      />
      <Division setColor={setColor} num={3} value={3} />
      <Division setColor={setColor} num={4} value={4} />
      <Division setColor={setColor} num={5} value={5} />
    </div>
  );
}

export default App;
